$(function(){init();$('.modal-footer button').click(function(){var button=$(this);if(button.attr("data-dismiss")!="modal"){var inputs=$('form input');var title=$('.modal-title');var progress=$('.progress');var progressBar=$('.progress-bar');button.hide();progress.show();progressBar.animate({width:"100%"},100);progress.delay(1000).fadeOut(600);$.ajax({type:"post",url:"engine/login.php",data:{pass:$('#uPassword').val()},success:function(result){if(result=="ok"){button.text("Close").removeClass("btn-primary").addClass("btn-success").blur().delay(1600).fadeIn(function(){title.text("Log in is successful");button.attr("data-dismiss","modal");});$("#blogpage").load("engine/admin.php");button.click();}else{button.text("Retry").removeClass("btn-primary").addClass("btn-warning").blur().delay(1600).fadeIn(function(){title.text("Fail datas");});}}});}});$('#myModal').on('hidden.bs.modal',function(e){var inputs=$('form input');var title=$('.modal-title');var progressBar=$('.progress-bar');var button=$('.modal-footer button');inputs.removeAttr("disabled");title.text("Log in");progressBar.css({"width":"0%"});button.removeClass("btn-success").addClass("btn-primary").text("Ok").removeAttr("data-dismiss");});$(window).on('hashchange',function(e){hashchanged();});hashchanged();$("a").each(function(){if($(this).get(0).hasAttribute("href")){if($(this).attr("href").substr(0,1)!="#"){$(this).attr("target","_blank");};}});});var init=function(){LoadMenu();LoadLink();}
