function unixTime(unixtime){var u=new Date(unixtime*1000);return u.getUTCFullYear()+'-'+('0'+u.getUTCMonth()).slice(-2)+'-'+('0'+u.getUTCDate()).slice(-2)+' '+('0'+u.getUTCHours()).slice(-2)+':'+('0'+u.getUTCMinutes()).slice(-2)+':'+('0'+u.getUTCSeconds()).slice(-2)+'.'+(u.getUTCMilliseconds()/1000).toFixed(1).slice(2,3)};function videoleker(){var sumsize=0;var jqxhr=$.post("http://online.duf.hu/jsondata.php",{}).done(function(data){$("#videodata").html(data);}).fail(function(){alert("error");});jqxhr.always(function(){$('#datatables').DataTable({"bPaginate":false,"bStateSave":true});rows=$("#videodata > tr");$(rows).each(function(){mts=$(this).find("td").eq(1);if(parseInt(mts.text())>0){mts.text(unixTime(mts.text()));}size=$(this).find("td").eq(3);sumsize+=parseInt(size.text());size.text((parseInt(size.text())/1024/1024).toFixed(2)+' MB');});});$("#videomeret").text((sumsize/1024/1024).toFix(2)+" MB");}